# simulation/tenderly_client.py
import aiohttp
from typing import Dict, List, Optional, Any
from dataclasses import dataclass
from graph_engine.models import YieldGraphEdge

@dataclass
class SimulationResult:
    success: bool
    gas_used: Optional[int] = None
    gas_cost_usd: Optional[float] = None
    output_amount: Optional[float] = None
    revert_reason: Optional[str] = None
    call_trace: Optional[Dict] = None
    price_impact: Optional[float] = None
    slippage_estimate: Optional[float] = None
    warnings: List[str] = None

@dataclass
class CallGraphResult:
    nodes: List[Dict]  # Contract calls
    edges: List[Dict]  # Call relationships
    edge_templates: List[Dict]  # Extracted edge patterns
    gas_analysis: Dict
    value_flows: List[Dict]

class TenderlySimulator:
    def __init__(self, api_key: str, project_slug: str, username: str):
        self.api_key = api_key
        self.project_slug = project_slug
        self.username = username
        self.base_url = "https://api.tenderly.co/api/v1"
        self.session = None

    async def initialize(self):
        self.session = aiohttp.ClientSession(
            headers={"X-Access-Key": self.api_key}
        )

    async def simulate_edge(self, edge: YieldGraphEdge, state: Dict, chain_id: int) -> SimulationResult:
        # Simulate single edge execution
        simulation_data = {
            "network_id": str(chain_id),
            "from": state.get("from_address"),
            "to": edge.metadata.get("contract_address"),
            "gas": 8000000,
            "gas_price": "0",
            "value": "0",
            "input": self._encode_edge_calldata(edge, state),
            "save_if_fails": True
        }

        url = f"{self.base_url}/account/{self.username}/project/{self.project_slug}/simulate"

        async with self.session.post(url, json=simulation_data) as response:
            if response.status == 200:
                result = await response.json()
                return self._parse_simulation_result(result, edge)
            else:
                return SimulationResult(success=False, revert_reason=f"API Error: {response.status}")

    async def simulate_path(self, path: List[YieldGraphEdge], state: Dict, chain_id: int) -> SimulationResult:
        # Simulate entire arbitrage path using fork
        # Create fork, execute path step by step
        fork_id = await self._create_fork(chain_id, state.get("block_number"))

        try:
            current_state = state.copy()
            total_gas = 0

            for i, edge in enumerate(path):
                step_result = await self._simulate_edge_on_fork(edge, current_state, fork_id)
                if not step_result.success:
                    return SimulationResult(
                        success=False,
                        revert_reason=f"Step {i+1} failed: {step_result.revert_reason}"
                    )

                total_gas += step_result.gas_used or 0
                current_state = self._update_state_from_result(current_state, step_result)

            return SimulationResult(
                success=True,
                gas_used=total_gas,
                output_amount=current_state.get("final_amount"),
                call_trace=current_state.get("trace")
            )

        finally:
            await self._delete_fork(fork_id)

    async def extract_call_graph(self, tx_hash: str, chain_id: int) -> CallGraphResult:
        # Extract call graph from historical transaction
        url = f"{self.base_url}/account/{self.username}/project/{self.project_slug}/trace/{tx_hash}"

        async with self.session.get(url) as response:
            if response.status == 200:
                trace_data = await response.json()
                return self._parse_call_trace_to_graph(trace_data)
            else:
                raise Exception(f"Failed to fetch trace: {response.status}")

# simulation/edge_validator.py
class EdgeValidator:
    def __init__(self, tenderly_client: TenderlySimulator, local_fallback=None):
        self.tenderly = tenderly_client
        self.local_fallback = local_fallback

    async def validate_edge_viability(self, edge: YieldGraphEdge, current_state: Dict) -> SimulationResult:
        try:
            # Try Tenderly first
            result = await self.tenderly.simulate_edge(edge, current_state, edge.chain_id)
            return result
        except Exception as e:
            if self.local_fallback:
                # Fallback to local simulation
                return await self.local_fallback.simulate_edge(edge, current_state)
            else:
                return SimulationResult(success=False, revert_reason=f"Simulation failed: {e}")

